  background: #f1f5f9;
  border-radius: 6px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, #10b981, #059669);
  border-radius: 6px;
  border: 2px solid #f1f5f9;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(180deg, #059669, #047857);
}

/* Firefox */
html {
  scrollbar-width: thin;
  scrollbar-color: #10b981 #f1f5f9;
}

:root {
  /* Meow Meow Pet Shop Color Palette */
  --meow-yellow: hsl(54, 100%, 67%);     /* #FFDE59 - Primary yellow */
  --meow-green: hsl(125, 25%, 30%);      /* #26732D - Primary green */
  --meow-green-dark: hsl(125, 38%, 23%); /* #1E5D26 - Darker green */
  --meow-blue: hsl(217, 91%, 60%);       /* #3B82F6 - Accent blue */
  --meow-light-bg: hsl(60, 9%, 98%);     /* #FAFAFA - Light background */
  --meow-gray: hsl(218, 11%, 65%);       /* #9CA3AF - Gray text */

  /* Default light theme colors */
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(54, 100%, 67%);
  --primary-foreground: hsl(125, 25%, 15%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(25, 5.3%, 44.7%);
  --accent: hsl(125, 47%, 18%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(24.6, 95%, 53.1%);
  --radius: 0.5rem;
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(54, 100%, 67%);
  --primary-foreground: hsl(125, 25%, 15%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(125, 47%, 18%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', sans-serif;
  }
}

@layer utilities {
  .meow-yellow {
    background-color: var(--meow-yellow);
  }

  .meow-green {
    background-color: var(--meow-green);
  }

  .meow-green-dark {
    background-color: var(--meow-green-dark);
  }

  .text-meow-yellow {
    color: var(--meow-yellow);
  }

  .text-meow-green {
    color: var(--meow-green);
  }

  .text-meow-green-dark {
    color: var(--meow-green-dark);
  }

  .border-meow-yellow {
    border-color: var(--meow-yellow);
  }

  .border-meow-green {
    border-color: var(--meow-green);
  }

  .hover-meow-yellow:hover {
    background-color: hsl(54, 100%, 60%);
  }

  .hover-meow-green:hover {
    background-color: var(--meow-green-dark);
  }

  /* Consistent section spacing utilities */
  .section-spacing {
    @apply py-12 md:py-16;
  }

  .section-spacing-small {
    @apply py-8 md:py-12;
  }

  /* Enhanced animations and transitions */
  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .animate-scale-up {
    animation: scaleUp 0.3s ease-out;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out;
  }

  /* Improved hover effects */
  .hover-lift {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift:hover {
    transform: translateY(-4px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
  }

  .btn-bounce {
    transition: transform 0.1s ease;
  }

  .btn-bounce:active {
    transform: scale(0.95);
  }

  /* Line clamp utility */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Responsive utilities */
  .responsive-grid {
    @apply grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 md:gap-6;
  }

  .responsive-container {
    @apply container mx-auto px-4 sm:px-6 lg:px-8;
  }

  /* Responsive sidebar utilities */
  .sidebar-transition {
    transition: width 0.3s ease-in-out, margin 0.3s ease-in-out;
  }

  .main-content-responsive {
    transition: margin-left 0.3s ease-in-out, width 0.3s ease-in-out;
  }

  /* Mobile sidebar overlay */
  @media (max-width: 1023px) {
    .sidebar-mobile {
      position: fixed;
      top: 0;
      left: 0;
      height: 100vh;
      z-index: 50;
      transform: translateX(-100%);
      transition: transform 0.3s ease-in-out;
    }

    .sidebar-mobile.open {
      transform: translateX(0);
    }
  }
}

/* Keyframe animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleUp {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes gentleBounce {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-2px);
  }
}

/* Hide scrollbars */
::-webkit-scrollbar {
  width: 0;
  height: 0;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: transparent;
}

::-webkit-scrollbar-thumb:hover {
  background: transparent;
}

/* Hide scrollbars for Firefox */
html {
  scrollbar-width: none;
}

/* Hide scrollbars for all elements */
* {
  scrollbar-width: none;
  -ms-overflow-style: none;
}

/* Ensure proper header positioning */
body {
  margin: 0;
  padding: 0;
}

/* Prevent layout shifts with sticky header */
.sticky {
  position: sticky !important;
}

/* Custom animations */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0; 
    transform: translateY(20px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes scaleUp {
  from { 
    opacity: 0; 
    transform: scale(0.9); 
  }
  to { 
    opacity: 1; 
    transform: scale(1); 
  }
}

.animate-fade-in {
  animation: fadeIn 0.6s ease-out;
}

.animate-slide-up {
  animation: slideUp 0.6s ease-out;
}

.animate-scale-up {
  animation: scaleUp 0.6s ease-out;
}

.btn-bounce:hover {
  animation: bounce 0.6s ease-in-out;
}

@keyframes bounce {
  0%, 20%, 60%, 100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-10px);
  }
  80% {
    transform: translateY(-5px);
  }
}

.hover-lift {
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.hover-lift:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

/* Responsive Banner Styles */
.hero-banner {
  position: relative;
  width: 100%;
  overflow: hidden;
}

.hero-banner img {
  width: 100%;
  height: auto;
  object-fit: cover;
  display: block;
}

/* Media queries for responsive banner */
@media (max-width: 768px) {
  .hero-banner img {
    object-fit: contain;
    min-height: 200px;
    max-height: 400px;
  }

  .hero-banner .overlay-content {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    z-index: 10;
  }
}

@media (min-width: 769px) and (max-width: 1024px) {
  .hero-banner img {
    object-fit: contain;
    min-height: 300px;
    max-height: 500px;
  }
}

@media (min-width: 1025px) {
  .hero-banner img {
    object-fit: contain;
    max-height: 600px;
  }
}

/* Ensure banner maintains aspect ratio on all devices */
@media (max-width: 480px) {
  .hero-banner {
    aspect-ratio: 16/9;
  }

  .hero-banner img {
    object-fit: cover;
    height: 100%;
  }
}

/* Banner container responsive adjustments */
.banner-container {
  position: relative;
  width: 100%;
  max-width: 100vw;
  overflow: visible;
}

.banner-container::before {
  content: '';
  display: block;
  padding-top: 56.25%; /* 16:9 aspect ratio */
}

.banner-container img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: contain;
}

@media (min-width: 768px) {
  .banner-container img {
    object-fit: contain;
  }
}

