import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Star } from "lucide-react";
import { useCart } from "@/hooks/use-cart";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";

export default function BestSellers() {
  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ['/api/products/best-sellers'],
  });

  const { addToCart } = useCart();
  const { toast } = useToast();

  const handleAddToCart = (product: Product) => {
    addToCart(product.id);
    toast({
      title: "Added to cart!",
      description: `${product.name} has been added to your cart.`,
    });
  };

  const renderStars = (rating: string) => {
    const numRating = parseFloat(rating);
    const stars = [];

    for (let i = 1; i <= 5; i++) {
      if (i <= numRating) {
        stars.push(<Star key={i} className="w-3 h-3 fill-yellow-400 text-yellow-400" />);
      } else if (i - 0.5 <= numRating) {
        stars.push(<Star key={i} className="w-3 h-3 fill-yellow-400/50 text-yellow-400" />);
      } else {
        stars.push(<Star key={i} className="w-3 h-3 text-gray-300" />);
      }
    }

    return stars;
  };

  if (isLoading) {
    return (
      <section className="py-12 bg-white">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="bg-gray-100 rounded-lg h-64 animate-pulse border"></div>
            ))}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="py-12 bg-white">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
          {products.map((product) => (
            <Card
              key={product.id}
              className="bg-white shadow-sm hover:shadow-md transition-shadow border rounded-lg"
            >
              <CardContent className="p-3">
                <div className="relative mb-3">
                  <img
                    src={product.image || ""}
                    alt={product.name}
                    className="w-full h-32 object-cover rounded-lg"
                  />
                </div>
                <Badge className="bg-purple-100 text-purple-700 mb-2 text-xs">
                  Cat Food
                </Badge>
                <h4 className="font-medium mb-2 text-xs text-gray-800 line-clamp-2">{product.name}</h4>
                <div className="flex items-center mb-2">
                  <div className="flex">
                    {renderStars(product.rating || "0")}
                  </div>
                  <span className="text-xs text-gray-500 ml-1">({product.rating})</span>
                </div>
                <div className="space-y-1">
                  <span className="text-sm font-bold text-purple-600">à§³{product.price}</span>
                  <Button
                    onClick={() => handleAddToCart(product)}
                    className="w-full bg-purple-600 text-white hover:bg-purple-700 text-xs py-1 h-7"
                  >
                    Add to Cart
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
}

