                    </div>
                  ) : (
                    <Link href={item.path}>
                      <Button variant="ghost" className="text-gray-700 hover:text-[#26732d] font-medium" data-testid={`nav-${item.name.toLowerCase().replace(/\s+/g, '-')}`}>
                        {item.name}
                      </Button>
                    </Link>
                  )}
                </div>
              ))}
            </div>
          </nav>
        </div>
      </header>


    </>
  );
}

